import prod from './data'; // Import the product data

function App() { // Main App component
  return (
    <div> // Container for the app
      <header>
        <a href="/">amazona</a> {/* This is a link */}
      </header>
      {/* Main content section */}
      <main>
        <h1>list of products</h1>
        <div className="products"> {/* Container for product list */}
        {/* Map through the products and create a product card for each */}
          {prod.products.map((product) => (
            <div className="product" key={product.slug}>
              <a href={`/product/${product.slug}`}>
                <img src={product.image} alt={product.name} /> {/* Product image link */}
              </a>
              <div className="product-details"> {/* Product details container */}
                <a href={`/product/${product.slug}`}>
                  <p>{product.name}</p>
                </a>
                <p>
                  <strong>${product.price}</strong>
                </p>
                <button>Add to cart</button>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
