import prod from './data'; // Import the product data

import {BroswerRouter, Route, Router} from 'react-router-dom'
import HomePage from './pages/HomePage';
function App() {
  // Main App component
  return (
    <BrowserRouter>
    <div>
      <header>
        <a href="/">amazona</a> {/* This is a link */}
      </header>
      {/* Main content section */}
      <main>
        <Router>
          {/* HomePage instead of HomeScre */}
          <Route path="/" element={<HomePage/>}/> 
        </Router>
        <h1>list of products</h1>
        {/* Container for product list */}
        <div className="products">
          {/* Map through the products and create a product card for each */}
          {prod.products.map((product) => (
            <div className="product" key={product.slug}>
              <a href={`/product/${product.slug}`}>
                <img src={product.image} alt={product.name} />
              </a>
              {/* Product details container */}
              <div className="product-details">
                <a href={`/product/${product.slug}`}>
                  <p>{product.name}</p>
                </a>
                <p>
                  <strong>${product.price}</strong>
                </p>
                <button>Add to cart</button>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
    </BrowserRouter>
  );
}
// Export the App component
export default App;
